VALUES('7c0971ab-9494-4008-8615-ea115ca0fd86', 'Account creation', 'accountCreation', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional //EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"> <html xmlns="http://www.w3.org/1999/xhtml" xmlns:v="urn:schemas-microsoft-com:vml" xmlns:o="urn:schemas-microsoft-com:office:office"> <head> <meta http-equiv="Content-Type" content="text/html; charset=utf-8"> <meta name="viewport" content="width=device-width"> <meta http-equiv="X-UA-Compatible" content="IE=edge"> <title></title> <style type="text/css"> body { margin: 0; padding: 0; } table, td, tr { vertical-align: top; border-collapse: collapse; } * { line-height: inherit; } a[x-apple-data-detectors=true] { color: inherit !important; text-decoration: none !important; } </style> <style type="text/css" id="media-query"> @media (max-width: 520px) { .block-grid, .col { min-width: 320px !important; max-width: 100% !important; display: block !important; } .block-grid { width: 100% !important; } .col { width: 100% !important; } .col_cont { margin: 0 auto; } img.fullwidth, img.fullwidthOnMobile { width: 100% !important; } .no-stack .col { min-width: 0 !important; display: table-cell !important; } .no-stack.two-up .col { width: 50% !important; } .no-stack .col.num2 { width: 16.6% !important; } .no-stack .col.num3 { width: 25% !important; } .no-stack .col.num4 { width: 33% !important; } .no-stack .col.num5 { width: 41.6% !important; } .no-stack .col.num6 { width: 50% !important; } .no-stack .col.num7 { width: 58.3% !important; } .no-stack .col.num8 { width: 66.6% !important; } .no-stack .col.num9 { width: 75% !important; } .no-stack .col.num10 { width: 83.3% !important; } .video-block { max-width: none !important; } .mobile_hide { min-height: 0px; max-height: 0px; max-width: 0px; display: none; overflow: hidden; font-size: 0px; } .desktop_hide { display: block !important; max-height: none !important; } } </style> </head> <body class="clean-body" style="margin: 0; padding: 0; -webkit-text-size-adjust: 100%; background-color: #FFFFFF;"> <table class="nl-container" style="table-layout: fixed; vertical-align: top; min-width: 320px; border-spacing: 0; border-collapse: collapse; mso-table-lspace: 0pt; mso-table-rspace: 0pt; background-color: #FFFFFF; width: 100%;" cellpadding="0" cellspacing="0" role="presentation" width="100%" bgcolor="#FFFFFF" valign="top"> <tbody> <tr style="vertical-align: top;" valign="top"> <td style="word-break: break-word; vertical-align: top;" valign="top"> <div style="background-color:transparent;"> <div class="block-grid " style="min-width: 320px; max-width: 500px; overflow-wrap: break-word; word-wrap: break-word; word-break: break-word; Margin: 0 auto; background-color: transparent;"> <div style="border-collapse: collapse;display: table;width: 100%;background-color:transparent;"> <div class="col num12" style="min-width: 320px; max-width: 500px; display: table-cell; vertical-align: top; width: 500px;"> <div class="col_cont" style="width:100% !important;"> <div style="border-top:0px solid transparent; border-left:0px solid transparent; border-bottom:0px solid transparent; border-right:0px solid transparent; padding-top:5px; padding-bottom:5px; padding-right: 0px; padding-left: 0px;"> <div style="font-size:16px;text-align:center;font-family:Arial, Helvetica Neue, Helvetica, sans-serif"> <div><img src="https://marketing.yoroflow.com/assets/logo/newlogo3.svg" width="40%" style=" display: block;margin-left: auto;margin-right: auto;margin-bottom: 10px;" /> <div style="display: block;margin-left: auto;margin-right: auto;width:95%;box-shadow: 0px -2px 19px 0px rgba(81,76,76,0.75);-webkit-box-shadow: 0px -2px 19px 0px rgba(81,76,76,0.75);-moz-box-shadow: 0px -2px 19px 0px rgba(81,76,76,0.75);background-color: transparent;border-radius: 15px;"> <div style="padding-left:30px;padding-top:5px;background-image: url(''https://marketing.yoroflow.com/assets/images/email_template_banner.svg'');background-size: cover;border-top-left-radius: 15px;border-top-right-radius: 15px;padding-bottom: 15px;"> <h1 style="color:white;font-weight:600;margin-bottom:5px;font-size: 25px;text-align:left"> Verify Your</h1> <h2 style="color:white;margin-top:0px;margin-bottom:5px;font-size:20px;text-align:left"> Email Address</h2> </div> <div style="padding:0px 30px"> <div style="display:-ms-flexbox;display:flex;-ms-flex-wrap:wrap;flex-wrap:wrap;padding-right:30px;"> <span style="width:100%;display:inline-block;"> <h1 style="margin: 0px;margin-top:15px;font-size: 20px;text-align:left;margin-bottom:15px;width:88%"> Hey {{firstName}} {{lastName}}, you’re almost ready to start enjoying Yoroflow!</h1> <p style="color:#333;margin-bottom: 15px;text-align:center"> Please confirm that you want to use this as your Yoroflow account email address. Once it’s done, you will be able to start using! </p> </span> </div> <a href="https://marketing.yoroflow.com/en/board/account-creation/{{accountDetailsId}}" rel="noopener noreferrer" target="_blank" style="text-decoration: none;padding: 8px 15px;border-radius:30px;color:#fff;margin-left: auto;margin-right: auto;display: block;width:90%;font-size:20px;text-align: center;background-image: url(''https://marketing.yoroflow.com/assets/images/email_template_button.svg'');background-size: cover;">Verify My Email</a><br /> <br /> <p style="text-align:left;color:#333;margin-bottom: 15px;margin-top:15px;text-align:center"> Ignore this mail, if you didn''t make a request.</p> </div> <div style="margin-bottom:15px;display:-ms-flexbox;display:flex;-ms-flex-wrap:wrap;flex-wrap:wrap;padding-right:30px;padding-left: 30px;padding-bottom:0px;margin-right: auto;margin-left: auto;width: 40%;"> <span style="display:inline-block;width: 33.33%;"><img src="https://marketing.yoroflow.com/assets/images/email_template_fb.svg" width="80%" style=" display: block;margin-left: auto;margin-right: auto;" /></span><span style="display:inline-block;width: 33.33%;"><img src="https://marketing.yoroflow.com/assets/images/email_template_twitter.svg" width="80%" style=" display: block;margin-left: auto;margin-right: auto;" /></span><span style="display:inline-block;width: 33.33%;"><img src="https://marketing.yoroflow.com/assets/images/email_template_linkedIn.svg" width="80%" style=" display: block;margin-left: auto;margin-right: auto;" /></span> </div> <p style="color:#333;text-align:center;font-size: 13px;line-height:20px"> Copyright © 2021 Yorosis Technologies Inc.<br /> All rights reserved.</p> <p style="color:#333;text-align:center;font-size: 13px;line-height:20px"> If you do not wish to receive these emails, <a href="https://marketing.yoroflow.com/user-profile" target="blank" style="text-decoration: none;color:#0265ff;display:inline-block;line-height:20px"> unsubscribe here</a>.</p><img src="https://marketing.yoroflow.com/assets/logo/newlogo3.svg" width="20%" style=" display: block;margin-left: auto;margin-right: auto;padding-bottom:30px" /> </div> </div> </div> </div> </div> </div> </div> </div> </div> </td> </tr> </tbody> </table> </body> </html>', '${customerUserId}', '2021-12-08 11:36:59.013', '${customerUserId}', '2021-12-10 12:54:15.042', 'Y', '${tenantId}', 'Y', 'Account creation verification');

VALUES('fd7c0bbc-2559-41bc-b1bc-4d8e0a45f204', 'Sales Team Notification', 'salesTeamNotification', '<p>Hello,</p><p>	New account has been created with details of,</p><p>	First Name             : <strong>{{firstName}},</strong></p><p>	Last Name             : <strong>{{lastName}},</strong></p><p>	Email                     : <strong>{{email}},</strong></p><p>	Phone Number      :  <strong>{{phoneNumber}},</strong></p><p>	Subdomain Name :  <strong>{{subdomainName}},</strong></p><p>	Trial Start Date     :  <strong>{{trialStartDate}},</strong></p><p>	Trial End Date      :  <strong>{{trialEndDate}}</strong></p><p>	</p><p>Thanks,</p><p>Yoroflow</p>', '${customerUserId}', '2021-12-10 15:47:56.165', '${customerUserId}', '2021-12-10 15:47:56.165', 'Y', '${tenantId}', 'Y', 'New account created');

VALUES('db7fc700-fe2c-4332-84bc-4e7435a42a63', 'Signup Template', 'signupTemplate', '<p>Hello,</p><p>New customer has been signup with Yoroflow and the details of,</p><p>First Name : <strong>{{firstName}},</strong></p><p>Last Name : <strong>{{lastName}},</strong></p><p>Email : <strong>{{email}},</strong></p><p>Phone Number: <strong>{{phoneNumber}}</strong></p><p><br></p><p>Thanks,</p><p>Yoroflow</p>', '${customerUserId}', '2021-12-10 15:43:45.812', '${customerUserId}', '2021-12-10 15:48:14.429', 'Y', '${tenantId}', 'Y', 'Signup details');
